  #{cityname,jdbcType=VARCHAR}, #{province,jdbcType=VARCHAR}, #{tyname,jdbcType=CHAR}
  )

mapper.update:

update area_item
set LEVELNAME = #{levelname,jdbcType=VARCHAR},
  CITYNAME = #{cityname,jdbcType=VARCHAR},
  PROVINCE = #{province,jdbcType=VARCHAR},
