mapper.update:

update bank_cnaps
set CNAPS_NAME = #{cnapsName,jdbcType=VARCHAR},
  CNAPS_ADDR = #{cnapsAddr,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=TIMESTAMP}
where CNAPS_CODE = #{cnapsCode,jdbcType=VARCHAR}

mapper.select:
select

from bank_cnaps


    and CNAPS_CODE=#{cnapsCode,jdbcType=VARCHAR}


    and CNAPS_NAME=#{cnapsName,jdbcType=VARCHAR}


    and CNAPS_ADDR=#{cnapsAddr,jdbcType=VARCHAR}


    and CREATE_TIME=#{createTime,jdbcType=TIMESTAMP}


    and LAST_UPDATE_TIME=#{lastUpdateTime,jdbcType=TIMESTAMP}
